use base64::prelude::BASE64_STANDARD;
use base64::Engine;
use criterion::{criterion_group, criterion_main, Criterion};
use std::{hint::black_box, ops::Deref, sync::LazyLock};
use ton_lib_core::boc::BoC;
use tonlib_core::cell::BagOfCells;

const ITERATIONS_COUNT: usize = 100;

static BOC_BYTES: LazyLock<Vec<u8>> = LazyLock::new(|| {
    let boc_base64 = "";
    BASE64_STANDARD.decode(boc_base64).unwrap()
});

static BOC_TOBLIB: LazyLock<BoC> = LazyLock::new(|| BoC::from_bytes(BOC_BYTES.deref()).unwrap());

static BOC_TOBLIB_CORE: LazyLock<BagOfCells> = LazyLock::new(|| BagOfCells::parse(&BOC_BYTES).unwrap());

fn boc_from_bytes_tonlib_core() {
    for _ in 0..ITERATIONS_COUNT {
        let boc = BagOfCells::parse(&BOC_BYTES).unwrap();
        black_box(boc);
    }
}

fn boc_from_bytes_tonlib() {
    for _ in 0..ITERATIONS_COUNT {
        let boc = BoC::from_bytes(BOC_BYTES.deref()).unwrap();
        black_box(boc);
    }
}

fn boc_to_bytes_tonlib_core() {
    for _ in 0..ITERATIONS_COUNT {
        let bytes = &BOC_TOBLIB_CORE.serialize(false).unwrap();
        black_box(bytes);
    }
}

fn boc_to_bytes_tonlib() {
    for _ in 0..ITERATIONS_COUNT {
        let bytes = &BOC_TOBLIB.to_bytes(false).unwrap();
        black_box(bytes);
    }
}

fn benchmark_functions(c: &mut Criterion) {
    c.bench_function("boc_from_bytes_tonlib_core", |b| b.iter(boc_from_bytes_tonlib_core));
    c.bench_function("boc_from_bytes_tonlib", |b| b.iter(boc_from_bytes_tonlib));

    c.bench_function("boc_to_bytes_tonlib_core", |b| b.iter(boc_to_bytes_tonlib_core));
    c.bench_function("boc_to_bytes_tonlib", |b| b.iter(boc_to_bytes_tonlib));
}

criterion_group!(benches, benchmark_functions);
criterion_main!(benches);
