use base64::prelude::BASE64_STANDARD;
use base64::Engine;
use std::sync::LazyLock;

pub const TON_MASTERCHAIN: i32 = -1;
pub const TON_SHARD_FULL: u64 = 0x8000_0000_0000_0000u64;
pub const TON_MAX_SPLIT_DEPTH: u8 = 60;

pub const TON_ZERO_CONFIG_BOC_B64: &str = "";
pub static ZERO_CONFIG_BOC: LazyLock<Vec<u8>> =
    LazyLock::new(|| BASE64_STANDARD.decode(TON_ZERO_CONFIG_BOC_B64).unwrap());
